mapper.select:
select count(articleno) from board
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select articleno, userid, subject, content, hit, regtime
from board
where articleno = #{articleno}

mapper.update:
update board
set hit = hit + 1
where articleno = #{articleno}

update board
set subject = #{subject}, content = #{content}
where articleno = #{articleno}
