mapper.select:
select id, name, password, profile_image_url
from user
where id = #{id} and password = #{password}

select id
from user
where id = #{id}

select id, name, password, profile_image_url
from user
where id = #{userid}

select token
from user
where id = #{userid}

mapper.update:
update user
set token = #{token}
where id = #{userid}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{userid}
